    public void deleteManguis(Connection con) {

        // Recuperem els boss que estan afectats
        String SQL = "select EMP_NO from EMPLE where APELLIDO like '%EZ%' and oficio like '%LIS%'";
        int num_esborrats = 0;
        try {

            Statement st = con.createStatement();

            ResultSet rst = st.executeQuery(SQL);

            // per a cada cap
            while (rst.next()) {
                int cap = rst.getInt(1);

                // esborrem els seus empleats
                String SQL2 = "delete from EMPLE where DIR=?";
                PreparedStatement pst = con.prepareStatement(SQL2);

                pst.setInt(1, cap);

                num_esborrats += pst.executeUpdate();
            }

            rst.close();

            System.out.println("S'han esborrat " + num_esborrats + " empleats");
        } catch (SQLException ex) {
            Logger.getLogger(App.class.getName()).log(Level.SEVERE, null, ex);
        }

    }

