    public ArrayList<Empleado> query62(Connection con, int year) {

        ArrayList<Empleado> empleados = new ArrayList<>();

        String SQL = "select * from EMPLE where year(FECHA_ALT)=?;";

        PreparedStatement pst;
        try {
            pst = con.prepareStatement(SQL);
            pst.setInt(1, year);

            ResultSet rst = pst.executeQuery();

            while (rst.next()) { // mientras hayan filas
                Empleado e = new Empleado(      // tienen 8 columnas
                        rst.getInt("EMP_NO"),
                        rst.getString("APELLIDO"),
                        rst.getString("OFICIO"),
                        rst.getInt("DIR"),
                        rst.getDate("FECHA_ALT"),
                        rst.getDouble("SALARIO"),
                        rst.getDouble("COMISION"),
                        rst.getInt("DEPT_NO"));

                empleados.add(e);
            }
        } catch (SQLException ex) {
            Logger.getLogger(App.class.getName()).log(Level.SEVERE, null, ex);
        }

        return empleados;
    }

